/* initialize start*/
body, p, h1, h2, h3, h4, h5, h6, ul, ol, li, dl, dt, dd,
table, th, td, form, fieldset, legend, input, textarea,
select, button, article, aside, canvas, details, figcaption,
figure, footer, header, hgroup, menu, nav, section, audio, video {
  margin: 0;
  padding: 0;
}
body {
  color: #333;
  font-style: normal;
  font-size: 18px;
  line-height: 1.666;
  font-family: AppleSDGothicNeo-Regular, 'notokr', '맑은고딕', Malgun Gothic, sans-serif;
  -webkit-text-size-adjust: none;
  -webkit-font-smoothing: antialiased;
}

h1, h2, h3, h4, h5, h6, dt, b, mark {
  font-weight: normal;
}
img, fieldset, iframe, button {
  border: 0 none;
}
li {
  list-style: none;
}
button,
mark {
  background: transparent;
  cursor: pointer;
  vertical-align: middle;
  font-family: AppleSDGothicNeo-Regular, 'notokr', '맑은고딕', Malgun Gothic, sans-serif;
}
table {
  border-collapse: collapse;
  border-spacing: 0;
}
th, td {
  border-collapse: collapse;
}
hr {
  display: none;
}
form {
  display: inline;
}
select, input, textarea {
  padding: 0;
  color: #333;
  font-size: 16px;
  font-family: AppleSDGothicNeo-Regular, 'notokr', '맑은고딕', Malgun Gothic, sans-serif;
  vertical-align: middle;
  resize: none;
  -webkit-appearance: none;
  border-radius: 0;
  border: none;
  box-sizing: border-box;
  outline: none;
  background-color: transparent;
}
input::-ms-clear,
select::-ms-expand {
  display: none;
}
i, em, u, cite, mark, address {
  font-style: normal;
}
a {
  text-decoration: none;
}
main {
  display: block;
}
input::-webkit-outer-spin-button,
input::-webkit-inner-spin-button {
  -webkit-appearance: none;
  margin: 0;
}
::placeholder {
  color: #bdbdbd;
  font-size: 16px;
}
::-webkit-input-placeholder {
  color: #bdbdbd;
  font-size: 16px;
}
:-moz-placeholder {
  color: #bdbdbd;
  font-size: 16px;
}
::-moz-placeholder {
  color: #bdbdbd;
  font-size: 16px;
}
:-ms-input-placeholder {
  color: #bdbdbd;
  font-size: 16px;
}
:disabled {
  opacity: 0.2;
}
html, body {
  height: 100%;
}
body {
  margin: 0;
  padding: 0;
  overflow-x: hidden;
  word-break: keep-all;
}
