/* buttons */
[class*='btnf-'] {
    display: inline-flex;
    min-width: 150px;
    height: 50px;
    padding: 0 25px;
    font-size: 18px;
    line-height: 2.1;
    font-weight: 700;
    letter-spacing: -0.025em;
    border: 1px solid #333;
    color: #333;
    border-radius: 25px;
    align-items: center;
    justify-content: center;
    box-sizing: border-box;
    vertical-align: middle;
    white-space: nowrap;
  }
  .btnf-line-gray {
    border-color: #ccc;
  }
  .btnf-line-orange {
    border-color: #e35205;
    color: #e35205;
  }
  .btnf-line-orange:disabled {
    border: 1px solid #333;
    color: #333;
  }
  .btnf-line-white {
    color: #fff;
    border-color: #fff;
  }
  .btnf-fill-orange {
    border-color: #e35205;
    color: #fff;
    background-color: #e35205;
  }
  .btnf-fill-orange:disabled {
    color: #fff;
    background-color: #333;
    border: 1px solid #333;
  }
  .btnf-fill-black {
    color: #fff;
    background-color: #333;
  }
  
  [class*='btnm-'] {
    display: inline-flex;
    min-width: 100px;
    height: 46px;
    padding: 0 10px;
    font-size: 16px;
    line-height: 2.1;
    letter-spacing: -0.025em;
    color: #fff;
    align-items: center;
    justify-content: center;
    box-sizing: border-box;
    vertical-align: middle;
  }
  .btnm-gray {
    background-color: #4a4d5b;
  }
  .btnm-black {
    background-color: #333;
  }
  .btnm-search {
    position: relative;
    min-width: auto !important;
    width: 46px;
    min-width: auto;
    font-size: 0;
    text-indent: -300px;
    overflow: hidden;
    background: #4a4d5b url('/images/2019/icon-search-address.png') no-repeat 50% 50%;
    background-size: 21px 21px;
  }
  .btnm-calendar {
    min-width: initial;
    width: 46px;
    background: #4a4d5b url('/images/2019/icon-calendar.png') no-repeat 50% 50%;
    background-size: 18px 20px;
    text-indent: -5000px;
    overflow: hidden;
  }
  
  [class*='btns-'] {
    display: inline-flex;
    min-width: 72px;
    height: 26px;
    padding: 0 14px;
    font-size: 14px;
    line-height: 1.8;
    font-weight: 700;
    color: #fff;
    border-radius: 13px;
    align-items: center;
    justify-content: center;
    box-sizing: border-box;
    vertical-align: middle;
  }
  .btns-fill-gray {
    border: 1px solid #4a4d5b;
    background-color: #4a4d5b;
  }
  .btns-fill-lightgray {
    border-color: 1px solid rgba(102, 102, 102, 0.66);
    background-color: rgba(102, 102, 102, 0.66);
  }
  .btns-fill-orange {
    border: 1px solid #e35205;
    background-color: #e35205;
  }
  .btns-fill-orange:disabled {
    background-color: #333;
    border-color: #333;
  }
  .btns-fill-navi {
    border: 1px solid #425563;
    background-color: #425563;
  }
  .btns-line-orange {
    border: 1px solid #e35205;
    color: #e35205;
  }
  .btns-line-gray {
    color: #333;
    border: 1px solid #333;
  }
  .btns-line-orange:disabled {
    border-color: #333;
    color: #333;
  }
  
  .btn-delete {
    /*width: 48px;
    height: 48px;*/
    width: 42px;
    height: 42px;
    text-indent: -5000px;
    overflow: hidden;
    background: url('/images/2019/btn-aside-close.png') no-repeat 50% 50%;
    background-size: 14px 14px;
  }
  .btn-play {
    width: 120px;
    height: 120px;
    background: url('/images/2019/btn-play.png') no-repeat;
    background-size: cover;
    overflow: hidden;
    text-indent: -999em;
  }
  @media (max-width: 767px) {
    [class*='btnf-'] {
      height: 44px;
      font-size: 16px;
      padding: 0 18px;
    }
    .btn-play {
      width: 60px;
      height: 60px;
    }
    [class*='btnm-'] {
      min-width: 78px;
    }
    [class*='btns-'] {
      min-width: 51px;
      height: 22px;
      font-size: 12px;
      padding: 0 9px;
      line-height: 1.1;
    }
  }
  
  .btnarea-foot {
    padding-top: 60px;
    font-size: 0;
    text-align: center;
  }
  .btnarea-foot [class*='btnf-'] {
    min-width: 150px;
    margin: 0 4px;
  }
  .btnarea-foot [class*='btnf-']:first-child {
    margin-left: 0;
  }
  .btnarea-foot [class*='btnf-']:last-child {
    margin-right: 0;
  }
  @media (max-width: 767px) {
    .btnarea-foot {
      display: flex;
      justify-content: center;
      padding-top: 40px;
    }
    .btnarea-foot [class*='btnf-'] {
      flex: 1;
      min-width: initial;
    }
  }

/* modal */
.modal-dimmed {
    position: fixed;
    left: 0;
    right: 0;
    top: 0;
    bottom: 0;
    background-color: rgba(0, 0, 0, 0.6);
    z-index: 80;
    opacity: 0;
    visibility: hidden;
    transition: opacity 0.5s, visibility 0.5s;
}
.modal-dimmed.show {
    opacity: 1;
    visibility: visible;
}
