	.video_last__area .text_02 .left__text,
	.video_last__area .text_02 .right__text { font-size: 20px; }
	.video_last__area .text_02 a::before { width: 100px; }
	.video_last__area .text_02 .right__text a::before { width: 75px; }

	.showroom__banner .swiper-slide .left__text, 
	.showroom__banner .swiper-slide .right__text { top: 41.5%; left: 10%; font-size: 46px; }
	.showroom__banner .swiper-slide .right__text { left: auto; right: 11.5%; }
	.showroom__banner .swiper-prev, 
	.showroom__banner .swiper-next { top: calc(50% - 60px); width: 40px; height: 60px; }
	.showroom__banner .swiper-pagination { bottom: 8%; }
	.yellow__content .text_02 p { font-size: 18px; }
	.yellow__content .text_03 li { font-size: 14px; }
	.yellow__content .yellow__video_01 .tooltip { margin: 0 0 8px 100px; }
	.yellow__content .yellow__video_02 .text_02 .left__text { margin: 0 0 0 20%; }
	.yellow__content .yellow__video_02 .text_02 .right__text { margin: 0 20% 0 0; }
	.yellow__content .yellow__video_04 .tooltip { margin: 0 0 8px 155px; }
	.white__content .white__video_02 .tooltip { margin: 0 0 8px 160px; }
	.navy__content .navy__video_03 .tooltip { margin: 0 0 8px 145px; }
	.navy__content .navy__video_04 .text__area h3 { width: 160px; }
	.navy__content .navy__video_04 .text__area h3 i { width: 70px; }
	.navy__content .navy__video_04 .text__area p { font-size: 14px; }
	.black__content h3 { font-size: 40px; }
	.black__content .black__video_02 .tooltip { margin: 0 0 8px 160px; }
	.black__content .black__video_06 .tooltip { margin: 0 0 8px 160px; }
	
	.layer__head { top: 3%; right: 3%; }
	.layer__cls { width: 30px; height: 30px; }
	.layer__body .title__area { margin: 34vh 0 0; }
	.layer__body h2 { font-size: 50px; }
	.layer__body h3 { font-size: 30px; }
	.layer__body h3 sup { font-size: 18px; }
	.layer__body .title__area p { font-size: 12px; }
	.layer__body .prev__text, 
	.layer__body .next__text { top: 34.5%; left: 3.5%; font-size: 16px; }
	.layer__body .next__text { left: auto; right: 4.5%; }
